    private function getCalendar($id, $name, $description, $ownerId)
    {
        $calendar = new Calendar();
        $calendar->setId($id);
        $calendar->setName($name);
        $calendar->setDescription($description);
        $calendar->setTimezone(0);
        $calendar->setOwnerId($ownerId);
        return $calendar;
    }

